//   from 'mdb-react-ui-kit';


const Login = () => {
  return (

    // <MDBContainer fluid>

    //   <MDBRow className='d-flex justify-content-center align-items-center h-100'>
    //     <MDBCol col='12'>

    //       <MDBCard className='bg-dark text-white my-5 mx-auto' style={{ borderRadius: '1rem', maxWidth: '400px' }}>
    //         <MDBCardBody className='p-5 d-flex flex-column align-items-center mx-auto w-100'>

    //           <h2 className="fw-bold mb-2 text-uppercase">Login</h2>
    //           <p className="text-white-50 mb-5">Please enter your login and password!</p>

    //           <MDBInput wrapperClass='mb-4 mx-5 w-100' labelClass='text-white' label='Email address' id='formControlLg' type='email' size="lg" />
    //           <MDBInput wrapperClass='mb-4 mx-5 w-100' labelClass='text-white' label='Password' id='formControlLg' type='password' size="lg" />

    //           <p className="small mb-3 pb-lg-2"><NavLink className="text-white-50" to="/home">Forgot password?</NavLink></p>
    //           <MDBBtn outline className='mx-2 px-5' color='white' size='lg'>
    //             Login
    //           </MDBBtn>

    //           <div className='d-flex flex-row mt-3 mb-5'>
    //             <MDBBtn tag='a' color='none' className='m-3' style={{ color: 'white' }}>
    //               <MDBIcon fab icon='facebook-f' size="lg" />
    //             </MDBBtn>

    //             <MDBBtn tag='a' color='none' className='m-3' style={{ color: 'white' }}>
    //               <MDBIcon fab icon='twitter' size="lg" />
    //             </MDBBtn>

    //             <MDBBtn tag='a' color='none' className='m-3' style={{ color: 'white' }}>
    //               <MDBIcon fab icon='google' size="lg" />
    //             </MDBBtn>
    //           </div>

    //           <div>
    //             <p className="mb-0">Don't have an account? <NavLink to="/signup" className="text-white-50 fw-bold">Sign Up</NavLink></p>

    //           </div>
    //         </MDBCardBody>
    //       </MDBCard>

    //     </MDBCol>
    //   </MDBRow>

    // </MDBContainer>

    <>
    <section className='login'>
        <div className='container mt-5'>
          <div className='login-content'>

          <div className='login-image'>
              <figure>
                <img src={loginpic} alt="" />
              </figure>
              <NavLink to="/signup" className="signup-image-link">Create Account</NavLink>
            </div>
            <div className='signup-form'>
              <h2 className='from-title'>Log-in</h2>
              <form className='signup-form' id='signup-form'>
                {/* <div className='form-group'>
                  <label htmlFor='name'>
                    <i class='bx bxs-user bx-sm'></i>
                  </label>
                  <input type="text" name="name" id="name" autoComplete="off" placeholder="Your Name" />
                </div> */}

                <div className='form-group'>
                  <label htmlFor='email'>
                    <i class='bx bxs-envelope bx-sm'></i>
                  </label>
                  <input type="email" name="email" id="email" autoComplete="off" placeholder="Your Email" />
                </div>

                {/* <div className='form-group'>
                  <label htmlFor='phone'>
                    <i class='bx bxs-phone bx-sm'></i>
                  </label>
                  <input type="number" name="phone" id="phone" autoComplete="off" placeholder="Your Number" />
                </div> */}

                {/* <div className='form-group'>
                  <label htmlFor='work'>
                    <i class='bx bxs-briefcase bx-sm'></i>
                  </label>
                  <input type="text" name="work" id="work" autoComplete="off" placeholder="Your Profession" />
                </div> */}

                <div className='form-group'>
                  <label htmlFor='password'>
                    <i class='bx bxs-lock-alt bx-sm'></i>
                  </label>
                  <input type="password" name="password" id="password" autoComplete="off" placeholder="Your Password" />
                </div>

                {/* <div className='form-group'>
                  <label htmlFor='cpassword'>
                    <i class='bx bxs-lock-alt bx-sm'></i>
                  </label>
                  <input type="password" name="cpassword" id="cpassword" autoComplete="off" placeholder="Confirm Your Password" />
                </div> */}

                <div className='form-group form-button'>
                  <input type="submit" name="login" id="login" className="form-submit" value="Login" />
                </div>

              </form>

            </div>

            

          </div>
        </div>
      </section>
    </>
  );
}


export default Login
